/* WYSIWYG Template Styles - Demo Version 1.0 */
* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

body {
    font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
    line-height: 1.6;
    color: #2c3e50;
    background-color: #ffffff;
    margin: 20px;
}

.document-header {
    display: flex;
    align-items: center;
    padding: 20px;
    border-bottom: 3px solid #3498db;
    margin-bottom: 30px;
    background: linear-gradient(135deg, #f8f9fa 0%, #e9ecef 100%);
}

.logo-section {
    margin-right: 30px;
}

.company-logo {
    max-height: 60px;
    max-width: 120px;
}

.title-section {
    flex: 1;
}

.title-section h1 {
    font-size: 28px;
    color: #2c3e50;
    margin-bottom: 10px;
    font-weight: 600;
}

.document-info {
    display: flex;
    gap: 30px;
    font-size: 14px;
    color: #5a6c7d;
}

.document-content {
    padding: 0;
}

section {
    margin-bottom: 40px;
    padding: 25px;
    background: #f8f9fa;
    border-radius: 8px;
    border-left: 4px solid #3498db;
}

section h2 {
    font-size: 22px;
    color: #2c3e50;
    margin-bottom: 20px;
    font-weight: 600;
}

section h3 {
    font-size: 18px;
    color: #34495e;
    margin-bottom: 15px;
    font-weight: 600;
}

.info-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
    gap: 15px;
    margin-bottom: 20px;
}

.info-item {
    display: flex;
    flex-direction: column;
    padding: 15px;
    background: white;
    border-radius: 6px;
    border: 1px solid #dee2e6;
}

.info-item label {
    font-size: 12px;
    font-weight: 600;
    text-transform: uppercase;
    color: #6c757d;
    margin-bottom: 5px;
    letter-spacing: 0.5px;
}

.info-item span {
    font-size: 16px;
    color: #2c3e50;
    font-weight: 500;
}

.highlight {
    background-color: #fff3cd;
    padding: 6px 10px;
    border-radius: 4px;
    border: 1px solid #ffeaa7;
    color: #856404 !important;
    font-weight: 700 !important;
}

.result-section {
    margin-top: 25px;
    padding: 20px;
    background: white;
    border-radius: 8px;
    border: 1px solid #dee2e6;
}

.result-badge {
    display: inline-block;
    padding: 10px 20px;
    border-radius: 25px;
    font-size: 18px;
    font-weight: 700;
    text-transform: uppercase;
    text-align: center;
    margin: 10px 0;
}

.result-megfelelő {
    background-color: #d4edda;
    color: #155724;
    border: 2px solid #c3e6cb;
}

.result-nem-megfelelő {
    background-color: #f8d7da;
    color: #721c24;
    border: 2px solid #f5c6cb;
}

.result-feltételesen-megfelelő {
    background-color: #fff3cd;
    color: #856404;
    border: 2px solid #ffeaa7;
}

.result-notes {
    margin-top: 15px;
    font-style: italic;
    color: #5a6c7d;
    line-height: 1.5;
}

.organization-info {
    border-left-color: #e74c3c;
}

.gate-info {
    border-left-color: #27ae60;
}

.inspection-info {
    border-left-color: #f39c12;
}

.document-footer {
    margin-top: 50px;
    padding: 25px;
    background: #2c3e50;
    color: white;
    border-radius: 8px;
}

.footer-content {
    display: flex;
    justify-content: space-between;
    align-items: center;
}

.generation-info p {
    margin-bottom: 5px;
    font-size: 14px;
}

.qr-section {
    text-align: center;
}

.qr-code {
    width: 60px;
    height: 60px;
    background: white;
    color: #2c3e50;
    border-radius: 8px;
    display: flex;
    align-items: center;
    justify-content: center;
    font-weight: bold;
    font-size: 18px;
    margin-bottom: 5px;
}

.qr-section small {
    font-size: 12px;
    opacity: 0.8;
}

@media print {
    body {
        margin: 0;
        background: white;
    }

    .document-header {
        background: white !important;
        border-bottom: 2px solid #3498db;
    }

    section {
        background: white !important;
        border: 1px solid #dee2e6;
        break-inside: avoid;
    }

    .document-footer {
        background: #f8f9fa !important;
        color: #2c3e50 !important;
        border: 1px solid #dee2e6;
    }
}

@media (max-width: 768px) {
    .document-header {
        flex-direction: column;
        text-align: center;
    }

    .logo-section {
        margin-right: 0;
        margin-bottom: 20px;
    }

    .document-info {
        flex-direction: column;
        gap: 10px;
    }

    .info-grid {
        grid-template-columns: 1fr;
    }

    .footer-content {
        flex-direction: column;
        gap: 20px;
        text-align: center;
    }
}

/* WYSIWYG Enhanced Template Styles - Demo Version 2.0 */
@import url('https://fonts.googleapis.com/css2?family=Inter:wght@300;400;500;600;700&display=swap');

* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

body {
    font-family: 'Inter', -apple-system, BlinkMacSystemFont, 'Segoe UI', sans-serif;
    line-height: 1.6;
    color: #1a202c;
    background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
    min-height: 100vh;
    padding: 20px;
}

.document-container {
    max-width: 210mm;
    margin: 0 auto;
    background: white;
    border-radius: 16px;
    overflow: hidden;
    box-shadow: 0 25px 50px rgba(0, 0, 0, 0.15);
}

.modern-header {
    position: relative;
    background: linear-gradient(135deg, #2d3748 0%, #4a5568 100%);
    color: white;
    padding: 40px;
    overflow: hidden;
}

.header-pattern {
    position: absolute;
    top: 0;
    right: 0;
    width: 300px;
    height: 300px;
    background: radial-gradient(circle, rgba(255,255,255,0.1) 1px, transparent 1px);
    background-size: 20px 20px;
    opacity: 0.3;
}

.header-content {
    position: relative;
    display: grid;
    grid-template-columns: auto 1fr auto;
    align-items: center;
    gap: 30px;
}

.company-logo {
    max-height: 80px;
    max-width: 160px;
    filter: brightness(0) invert(1);
}

.title-section h1 {
    font-size: 32px;
    font-weight: 700;
    margin-bottom: 8px;
}

.subtitle {
    font-size: 16px;
    opacity: 0.9;
    font-weight: 400;
}

.doc-metadata {
    text-align: right;
    display: flex;
    flex-direction: column;
    gap: 8px;
}

.doc-number {
    font-size: 18px;
    font-weight: 600;
    font-family: 'Courier New', monospace;
}

.doc-date {
    font-size: 14px;
    opacity: 0.8;
}

.doc-status {
    padding: 6px 12px;
    border-radius: 20px;
    font-size: 12px;
    font-weight: 600;
    text-transform: uppercase;
}

.status-megfelelő {
    background: rgba(72, 187, 120, 0.2);
    color: #22543d;
    border: 1px solid rgba(72, 187, 120, 0.3);
}

.document-main {
    padding: 40px;
}

.summary-cards {
    display: grid;
    grid-template-columns: repeat(3, 1fr);
    gap: 25px;
    margin-bottom: 40px;
}

.card {
    padding: 25px;
    border-radius: 16px;
    background: linear-gradient(135deg, #f8fafc 0%, #e2e8f0 100%);
    border: 1px solid rgba(226, 232, 240, 0.8);
    display: flex;
    align-items: center;
    gap: 16px;
}

.card-icon {
    font-size: 32px;
    width: 60px;
    height: 60px;
    display: flex;
    align-items: center;
    justify-content: center;
    border-radius: 12px;
    background: rgba(255, 255, 255, 0.8);
}

.card-content h3 {
    font-size: 14px;
    font-weight: 600;
    color: #4a5568;
    margin-bottom: 4px;
    text-transform: uppercase;
    letter-spacing: 0.5px;
}

.card-content h4 {
    font-size: 18px;
    font-weight: 700;
    color: #1a202c;
    margin-bottom: 4px;
}

.card-content p {
    font-size: 14px;
    color: #4a5568;
    margin-bottom: 4px;
}

.card-content small {
    font-size: 12px;
    color: #718096;
}

.details-section {
    background: #f7fafc;
    border-radius: 16px;
    padding: 30px;
    margin-bottom: 30px;
}

.section-header {
    display: flex;
    align-items: center;
    justify-content: space-between;
    margin-bottom: 25px;
}

.section-header h2 {
    font-size: 24px;
    font-weight: 700;
    color: #2d3748;
}

.inspection-badge {
    background: linear-gradient(135deg, #667eea, #764ba2);
    color: white;
    padding: 8px 16px;
    border-radius: 25px;
    font-size: 12px;
    font-weight: 600;
    text-transform: uppercase;
    letter-spacing: 0.5px;
}

.details-grid {
    display: grid;
    grid-template-columns: repeat(2, 1fr);
    gap: 25px;
}

.detail-panel {
    background: white;
    padding: 25px;
    border-radius: 12px;
    border: 1px solid #e2e8f0;
}

.detail-panel h4 {
    font-size: 16px;
    font-weight: 600;
    color: #2d3748;
    margin-bottom: 20px;
}

.timeline {
    position: relative;
}

.timeline::before {
    content: '';
    position: absolute;
    left: 12px;
    top: 20px;
    bottom: 20px;
    width: 2px;
    background: linear-gradient(to bottom, #667eea, #764ba2);
}

.timeline-item {
    display: flex;
    align-items: center;
    gap: 20px;
    margin-bottom: 20px;
    position: relative;
}

.timeline-marker {
    width: 24px;
    height: 24px;
    background: linear-gradient(135deg, #667eea, #764ba2);
    border-radius: 50%;
    border: 3px solid white;
    box-shadow: 0 0 0 2px #e2e8f0;
    z-index: 1;
}

.timeline-content {
    display: flex;
    flex-direction: column;
}

.timeline-content strong {
    font-weight: 600;
    color: #2d3748;
    margin-bottom: 2px;
}

.timeline-content span {
    color: #718096;
    font-size: 14px;
}

.checklist {
    display: flex;
    flex-direction: column;
    gap: 12px;
}

.check-item {
    display: flex;
    align-items: center;
    gap: 12px;
    padding: 12px;
    background: #f8f9fa;
    border-radius: 8px;
    border: 1px solid #e2e8f0;
}

.check-item.completed .check-mark {
    background: linear-gradient(135deg, #48bb78, #38a169);
    color: white;
}

.check-mark {
    width: 24px;
    height: 24px;
    border-radius: 50%;
    background: #e2e8f0;
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 12px;
    font-weight: bold;
}

.result-section {
    background: white;
    border-radius: 16px;
    padding: 30px;
    border: 1px solid #e2e8f0;
    box-shadow: 0 4px 6px rgba(0, 0, 0, 0.07);
}

.result-header h2 {
    font-size: 24px;
    font-weight: 700;
    color: #2d3748;
    margin-bottom: 25px;
}

.result-display {
    display: flex;
    align-items: center;
    gap: 25px;
    padding: 25px;
    border-radius: 16px;
    margin-bottom: 25px;
}

.result-megfelelő {
    background: linear-gradient(135deg, #f0fff4 0%, #c6f6d5 100%);
    border: 2px solid #9ae6b4;
}

.result-icon {
    font-size: 48px;
    width: 80px;
    height: 80px;
    display: flex;
    align-items: center;
    justify-content: center;
    border-radius: 50%;
    background: rgba(255, 255, 255, 0.8);
}

.result-content h3 {
    font-size: 28px;
    font-weight: 700;
    margin-bottom: 8px;
    color: #22543d;
}

.result-description {
    font-size: 16px;
    color: #4a5568;
    line-height: 1.6;
}

.notes-panel {
    background: #f7fafc;
    padding: 20px;
    border-radius: 12px;
    border-left: 4px solid #667eea;
}

.notes-panel h4 {
    font-size: 16px;
    font-weight: 600;
    color: #2d3748;
    margin-bottom: 12px;
}

.notes-content {
    font-size: 14px;
    color: #4a5568;
    line-height: 1.6;
    font-style: italic;
}

.document-footer {
    background: linear-gradient(135deg, #2d3748 0%, #4a5568 100%);
    color: white;
    padding: 30px 40px;
}

.footer-grid {
    display: grid;
    grid-template-columns: 1fr auto 1fr;
    gap: 40px;
    align-items: center;
}

.footer-section h5 {
    font-size: 14px;
    font-weight: 600;
    margin-bottom: 12px;
    color: #e2e8f0;
    text-transform: uppercase;
    letter-spacing: 0.5px;
}

.footer-section p {
    font-size: 13px;
    color: #cbd5e0;
    margin-bottom: 4px;
}

.signature-section {
    text-align: center;
}

.signature-box {
    padding: 20px;
    background: rgba(255, 255, 255, 0.1);
    border-radius: 12px;
}

.signature-line {
    font-size: 16px;
    font-weight: 600;
    color: white;
    border-bottom: 2px solid rgba(255, 255, 255, 0.3);
    padding-bottom: 8px;
    margin-bottom: 8px;
    min-width: 200px;
}

.qr-section {
    text-align: center;
}

.qr-code-enhanced {
    width: 80px;
    height: 80px;
    background: linear-gradient(135deg, #f8f9fa, #e2e8f0);
    border-radius: 12px;
    display: flex;
    align-items: center;
    justify-content: center;
    margin: 0 auto 8px;
    box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
}

.qr-placeholder {
    font-size: 24px;
    font-weight: bold;
    color: #2d3748;
}

@media print {
    body {
        background: none !important;
        padding: 0;
    }

    .document-container {
        box-shadow: none;
        border-radius: 0;
    }
}

@media (max-width: 768px) {
    .header-content {
        grid-template-columns: 1fr;
        text-align: center;
        gap: 20px;
    }

    .summary-cards {
        grid-template-columns: 1fr;
    }

    .details-grid {
        grid-template-columns: 1fr;
    }

    .result-display {
        flex-direction: column;
        text-align: center;
    }

    .footer-grid {
        grid-template-columns: 1fr;
        text-align: center;
        gap: 20px;
    }
}
